#include <iostream>

class Madarak
{

public:

    virtual void  repül()
    {
        std::cout << "Hiba\n" ;
    }
};

class Sasok : public Madarak
{
public:

    void repül() override
    {
        std::cout << "...\n";
    }
};

class Pingvinek : public Madarak
{
};

static void repülő(Madarak& b)
{
    b.repülő();
}

int main()
{
    Sasok Sas;
    Pingvinek Pingvin;

    repülő(Sas);
    repülő(Pingvin);

    return 0;
}


